import React, { useState } from "react";
import TodoItem from "./item/TodoItem";

const data = [
  {
    _id: "vmvnjf",
    title: "Finish the essay collaboration",
    isComplited: false,
  },
  {
    _id: "jhbgjhg",
    title: "Read next chapter f the book",
    isComplited: false,
  },
  {
    _id: "lfkld",
    title: "Send the finished assignment",
    isComplited: false,
  },
];

const Home = () => {
  const [todos, setTodos] = useState(data);

  const changeTodo = (id) => {
    const copy = [...todos];
    const current = copy.find((t) => t._id === id);
    current.isCompleted = !current.isCompleted;
    setTodos(copy);
  };

  const removeTodo = (id) => {
    setTodos([...todos].filter((t) => t._id !== id));
  };

  const addTodo = (title) => {
    setTodos([
      {
        _id: new Date(),
        title,
        isComplited: false,
      },
      ...todos,
    ]);
  };

  return (
    <div className="w-4/5 mx-auto">
      <h1 className="text-2xl font-bold text-center mb-10">To do for junior</h1>
      {todos.map((todo) => (
        <TodoItem
          key={todo._id}
          todo={todo}
          changeTodo={changeTodo}
          removeTodo={removeTodo}
        />
      ))}
      <CreateTodoField addTodo={addTodo}/>
    </div>
  );
};

export default Home;
